    <div style="position:absolute;top:211px;left:278px;height:42px;width:279px" class=""><p class="cls_003" style="font-size:23.1px;margin-top:8.01px;font-weight:bold;color:white;width:auto;text-align:right"><?= $user['name']?></p></div>
